import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import '../styles/UserProfile.css';

const UserProfile = () => {
  const navigate = useNavigate();
  const [userDetails, setUserDetails] = useState({
    name: '',
    email: '',
    address: '',
  });

  const [isEditing, setIsEditing] = useState(false);
  const [editedDetails, setEditedDetails] = useState(userDetails);
  const [showPopup, setShowPopup] = useState(false);

  useEffect(() => {
    const storedUser = JSON.parse(localStorage.getItem('user'));
    if (storedUser) {
      setUserDetails({
        name: storedUser.name || 'No Name',  // ✅ Get name
        email: storedUser.email || 'No Email',
        address: 'Not provided'  // Change if address is stored
      });
      setEditedDetails({
        name: storedUser.name || 'No Name',
        email: storedUser.email || 'No Email',
        address: 'Not provided'
      });
    }
  }, []);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setEditedDetails((prevDetails) => ({
      ...prevDetails,
      [name]: value,
    }));
  };

  const handleEditClick = () => {
    setIsEditing(true);
  };

  const handleCancelClick = () => {
    setIsEditing(false);
    setEditedDetails(userDetails);
  };

  const handleSaveClick = () => {
    setUserDetails(editedDetails);
    localStorage.setItem('user', JSON.stringify(editedDetails)); // Update local storage
    setIsEditing(false);
  };

  const handleLogout = () => {
    localStorage.removeItem('token'); // Remove JWT token
    localStorage.removeItem('user'); // Remove user data
    setShowPopup(true);
    setTimeout(() => {
      navigate('/login'); // Redirect to login page after logout
    }, 1000);
  };

  return (
    <div className="user-profile">
      <h1>User Profile</h1>

      <div className="profile-info">
        <div className="profile-item">
          <label>Name:</label>
          {isEditing ? (
            <input
              type="text"
              name="name"
              value={editedDetails.name}
              onChange={handleInputChange}
            />
          ) : (
            <p>{userDetails.name}</p>
          )}
        </div>

        <div className="profile-item">
          <label>Email:</label>
          <p>{userDetails.email}</p> {/* Email should not be editable */}
        </div>

        <div className="profile-item">
          <label>Address:</label>
          {isEditing ? (
            <input
              type="text"
              name="address"
              value={editedDetails.address}
              onChange={handleInputChange}
            />
          ) : (
            <p>{userDetails.address || 'Not provided'}</p>
          )}
        </div>
      </div>

      <div className="profile-actions">
        {isEditing ? (
          <>
            <button className="save-btn" onClick={handleSaveClick}>
              Save
            </button>
            <button className="cancel-btn" onClick={handleCancelClick}>
              Cancel
            </button>
          </>
        ) : (
          <button className="edit-btn" onClick={handleEditClick}>
            Edit
          </button>
        )}
      </div>

      <button className="logout-btn" onClick={handleLogout}>
        Logout
      </button>

      {showPopup && (
        <div className="logout-popup">
          <p>You have been logged out successfully!</p>
        </div>
      )}
    </div>
  );
};

export default UserProfile;
