import React, { useContext } from 'react';
import { CartContext } from '../CartContext';
import '../styles/Cart.css';
import apple from '../assets/ProductImages/apple.jpeg';
import banana from '../assets/ProductImages/banana.jpeg';
import cabbage from '../assets/ProductImages/cabbage.jpg';
import carrot from '../assets/ProductImages/carrot.jpg';
import grapes from '../assets/ProductImages/grapes.jpg';
import onion from '../assets/ProductImages/onion.jpg';
import orange from '../assets/ProductImages/orange.jpg';
import potato from '../assets/ProductImages/potato.jpg';
import tomato from '../assets/ProductImages/tomato.jpg';
import watermelon from '../assets/ProductImages/watermelon.jpg';


function Cart() {
  const { cart, setCart } = useContext(CartContext);

  const handleRemoveFromCart = (index) => {
    const updatedCart = cart.filter((_, i) => i !== index);
    setCart(updatedCart);
  };

  return (
    <div className="cart">
      <h2>Your Cart</h2>
      {cart.length > 0 ? (
        <ul>
          {cart.map((item, index) => (
            <li key={index} className="cart-item">
              <img 
                src={item.image} 
                alt={item.name} 
                className="cart-item-image" 
              />
              <div className="cart-item-details">
                <p className="cart-item-name">{item.name}</p>
                <p className="cart-item-price">${item.price.toFixed(2)}</p>
              </div>
              <button 
                className="remove-btn" 
                onClick={() => handleRemoveFromCart(index)}
              >
                Remove
              </button>
            </li>
          ))}
        </ul>
      ) : (
        <p>Your cart is empty</p>
      )}
    </div>
  );
}

export default Cart;
