import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom'; // Import the useNavigate hook
import '../styles/PaymentSelection.css';

const PaymentSelection = ({ onSubmit }) => {
  const [selectedMethod, setSelectedMethod] = useState('');
  const navigate = useNavigate(); // Initialize the navigate function

  const handlePaymentChange = (e) => {
    setSelectedMethod(e.target.value);
  };

  const handleFormSubmit = (e) => {
    e.preventDefault();
    if (!selectedMethod) {
      alert('Please select a payment method.');
      return;
    }
    console.log('Payment Method Submitted:', selectedMethod);
    onSubmit({ paymentMethod: selectedMethod });
    navigate('/orderreview'); // Redirect to the order review page
  };

  return (
    <form onSubmit={handleFormSubmit}>
      <h2>Select Payment Method</h2>
      <div className="payment-container">
        <label className="payment-option">
          <img
            src="https://upload.wikimedia.org/wikipedia/commons/b/b5/PayPal.svg"
            alt="PayPal"
            className="payment-logo"
          />
          <input
            type="radio"
            name="paymentMethod"
            value="PayPal"
            checked={selectedMethod === 'PayPal'}
            onChange={handlePaymentChange}
          />
          <span>PayPal</span>
        </label>

        <label className="payment-option">
          <img
            src="https://images.stripeassets.com/fzn2n1nzq965/6XFEUA9FzMBMphYdcUab19/37a1e07201366a351f7956560ccac09d/Stripe_wordmark_-_slate.svg?q=80&w=1082"
            alt="Stripe"
            className="payment-logo"
          />
          <input
            type="radio"
            name="paymentMethod"
            value="Stripe"
            checked={selectedMethod === 'Stripe'}
            onChange={handlePaymentChange}
          />
          <span>Stripe</span>
        </label>
      </div>
      <button type="submit" className="submit-btn">
        Next
      </button>
    </form>
  );
};

export default PaymentSelection;
