import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import '../styles/AdminProfile.css';

const AdminProfile = () => {
  const navigate = useNavigate();
  const [adminDetails, setAdminDetails] = useState({
    name: '',
    email: '',
  });

  const [isEditing, setIsEditing] = useState(false);
  const [editedDetails, setEditedDetails] = useState(adminDetails);
  const [showPopup, setShowPopup] = useState(false);
  const [isLoading, setIsLoading] = useState(true);

  useEffect(() => {
    const storedAdmin = JSON.parse(localStorage.getItem('admin'));
    console.log('Stored admin from localStorage:', storedAdmin);
    if (storedAdmin) {
      setAdminDetails({
        name: storedAdmin.name || 'No Name',
        email: storedAdmin.email || 'No Email',
      });
      setEditedDetails({
        name: storedAdmin.name || 'No Name',
        email: storedAdmin.email || 'No Email',
      });
    }
    setIsLoading(false);
  }, []);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setEditedDetails((prevDetails) => ({
      ...prevDetails,
      [name]: value,
    }));
  };

  const handleEditClick = () => {
    setIsEditing(true);
  };

  const handleCancelClick = () => {
    setIsEditing(false);
    setEditedDetails(adminDetails);
  };

  const handleSaveClick = () => {
    setAdminDetails(editedDetails);
    localStorage.setItem('admin', JSON.stringify(editedDetails));
    setIsEditing(false);
  };

  const handleLogout = () => {
    localStorage.removeItem('adminToken');
    localStorage.removeItem('admin');
    setShowPopup(true);
    setTimeout(() => {
      navigate('/');
    }, 1000);
  };

  if (isLoading) {
    return <p>Loading...</p>;
  }

  return (
    <div className="admin-profile">
      <h1>Admin Profile</h1>

      <div className="profile-info">
        <div className="profile-item">
          <label>Name:</label>
          {isEditing ? (
            <input
              type="text"
              name="name"
              value={editedDetails.name}
              onChange={handleInputChange}
            />
          ) : (
            <p>{adminDetails.name}</p>
          )}
        </div>

        <div className="profile-item">
          <label>Email:</label>
          {isEditing ? (
            <input
              type="email"
              name="email"
              value={editedDetails.email}
              onChange={handleInputChange}
            />
          ) : (
            <p>{adminDetails.email}</p>
          )}
        </div>
      </div>

      <div className="profile-actions">
        {isEditing ? (
          <>
            <button className="save-btn" onClick={handleSaveClick}>
              Save
            </button>
            <button className="cancel-btn" onClick={handleCancelClick}>
              Cancel
            </button>
          </>
        ) : (
          <button className="edit-btn" onClick={handleEditClick}>
            Edit
          </button>
        )}
      </div>

      <button className="logout-btn" onClick={handleLogout}>
        Logout
      </button>

      {showPopup && (
        <div className="logout-popup">
          <p>You have been logged out successfully!</p>
        </div>
      )}
    </div>
  );
};

export default AdminProfile;
