import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import '../styles/AdminProfile.css';

const AdminProfile = () => {
  const navigate = useNavigate();
  const [adminDetails, setAdminDetails] = useState({
    name: '',
    email: '',
    role: '',
  });

  const [isEditing, setIsEditing] = useState(false);
  const [editedDetails, setEditedDetails] = useState(adminDetails);
  const [showPopup, setShowPopup] = useState(false);
  const [isLoading, setIsLoading] = useState(true);

  useEffect(() => {
    const fetchAdminDetails = async () => {
      try {
        const adminId = localStorage.getItem('adminId'); // Retrieve admin ID from localStorage
        if (!adminId) {
          navigate('/'); // Redirect if admin is not logged in
          return;
        }

        const response = await axios.get(`http://localhost:5000/api/admins/${adminId}`);
        console.log('Admin details:', response.data);

        setAdminDetails({
          name: response.data.name || 'No Name',
          email: response.data.email || 'No Email',
          role: response.data.role || 'No Role',
        });

        setEditedDetails({
          name: response.data.name || 'No Name',
          email: response.data.email || 'No Email',
          role: response.data.role || 'No Role',
        });

        setIsLoading(false);
      } catch (error) {
        console.error('Error fetching admin details:', error);
        setIsLoading(false);
      }
    };

    fetchAdminDetails();
  }, [navigate]);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setEditedDetails((prevDetails) => ({
      ...prevDetails,
      [name]: value,
    }));
  };

  const handleEditClick = () => {
    setIsEditing(true);
  };

  const handleCancelClick = () => {
    setIsEditing(false);
    setEditedDetails(adminDetails);
  };

  const handleSaveClick = async () => {
    try {
      const adminId = localStorage.getItem('adminId');
      await axios.put(`http://localhost:5000/api/admins/${adminId}`, editedDetails);
      setAdminDetails(editedDetails);
      localStorage.setItem('adminDetails', JSON.stringify(editedDetails)); // Update local storage
      setIsEditing(false);
    } catch (error) {
      console.error('Error updating admin details:', error);
    }
  };

  const handleLogout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('adminId');
    setShowPopup(true);
    setTimeout(() => {
      navigate('/');
    }, 1000);
  };

  if (isLoading) {
    return <p>Loading...</p>;
  }

  return (
    <div className="admin-profile">
      <h1>Admin Profile</h1>

      <div className="profile-info">
        <div className="profile-item">
          <label>Name:</label>
          {isEditing ? (
            <input type="text" name="name" value={editedDetails.name} onChange={handleInputChange} />
          ) : (
            <p>{adminDetails.name}</p>
          )}
        </div>

        <div className="profile-item">
          <label>Email:</label>
          {isEditing ? (
            <input type="email" name="email" value={editedDetails.email} onChange={handleInputChange} />
          ) : (
            <p>{adminDetails.email}</p>
          )}
        </div>

        <div className="profile-item">
          <label>Role:</label>
          {isEditing ? (
            <input type="text" name="role" value={editedDetails.role} onChange={handleInputChange} />
          ) : (
            <p>{adminDetails.role}</p>
          )}
        </div>
      </div>

      <div className="profile-actions">
        {isEditing ? (
          <>
            <button className="save-btn" onClick={handleSaveClick}>Save</button>
            <button className="cancel-btn" onClick={handleCancelClick}>Cancel</button>
          </>
        ) : (
          <button className="edit-btn" onClick={handleEditClick}>Edit</button>
        )}
      </div>

      <button className="logout-btn" onClick={handleLogout}>Logout</button>

      {showPopup && (
        <div className="logout-popup">
          <p>You have been logged out successfully!</p>
        </div>
      )}
    </div>
  );
};

export default AdminProfile;
