import React, { useContext } from 'react';
import { CartContext } from '../../CartContext';
import '../styles/Cart.css';

function Cart() {
  const { cart } = useContext(CartContext);

  return (
    <div className="cart">
      <h2>Your Cart</h2>
      {cart.length > 0 ? (
        <ul>
          {cart.map((item, index) => (
            <li key={index}>
              {item.name} - ${item.price}
            </li>
          ))}
        </ul>
      ) : (
        <p>Your cart is empty</p>
      )}
    </div>
  );
}

export default Cart;
