import React, { useState } from 'react';
import '../styles/Home.css';

function Products() {
  const [cart, setCart] = useState([]);
  const [showPopup, setShowPopup] = useState(false);

  const products = [
    { id: 1, name: 'Fresh Apples', price: 2.99, img: 'assets/ProductImages/apple.jpeg' },
    { id: 2, name: 'Organic Bananas', price: 1.99, img: 'assets/ProductImages/banana.jpeg' },
    { id: 3, name: 'Black Grapes', price: 2.99, img: 'assets/ProductImages/grapes.jpg' },
    { id: 4, name: 'Oranges', price: 2.99, img: 'assets/ProductImages/orange.jpg' },
    { id: 5, name: 'Watermelon', price: 2.99, img: 'assets/ProductImages/watermelon.jpg' },
    { id: 6, name: 'Tomato', price: 2.99, img: 'assets/ProductImages/tomato.jpg' },
    { id: 7, name: 'Carrot', price: 2.99, img: 'assets/ProductImages/carrot.jpg' },
    { id: 8, name: 'Cabbage', price: 2.99, img: 'assets/ProductImages/cabbage.jpg' },
    { id: 9, name: 'Potato', price: 2.99, img: 'assets/ProductImages/potato.jpg' },
    { id: 10, name: 'Onion', price: 2.99, img: 'assets/ProductImages/onion.jpg' },
  ];

  const addToCart = (product) => {
    setCart((prevCart) => [...prevCart, product]);
    setShowPopup(true);

    // Automatically close popup after 2 seconds
    setTimeout(() => setShowPopup(false), 2000);
  };

  return (
    <div className="home-container">
      <h1>Fresh Fruits & Vegetables</h1>
      <div className="home-grid">
        {products.map((product) => (
          <div key={product.id} className="home-item">
            <img src={product.img} alt={product.name} />
            <h3>{product.name}</h3>
            <p>Only ${product.price} per kg</p>
            <button onClick={() => addToCart(product)}>Add to Cart</button>
          </div>
        ))}
      </div>

      
    </div>
  );
}

export default Products;
