import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import '../styles/Login.css';
import { Link } from 'react-router-dom';

function Login() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [errorMessage, setErrorMessage] = useState(''); // State to display error message
  const navigate = useNavigate();

  const handleLogin = async (e) => {
    e.preventDefault();
    try {
        const response = await axios.post('http://localhost:5000/api/auth/login', { email, password });

        console.log("Login response:", response.data); // Debugging

        const { token, user } = response.data;

        if (!user || !user.role) {
            setErrorMessage("User role not found. Please contact support.");
            return;
        }

        localStorage.setItem('token', token);
        localStorage.setItem('role', user.role);

        if (user.role === 'admin') {
            navigate('/admin-dashboard');
        } else if (user.role === 'user') {
            navigate('/user-dashboard');
        } else {
            setErrorMessage('Invalid role detected. Contact support.');
        }
    } catch (error) {
        console.error('Error during login:', error);
        setErrorMessage(error.response?.data?.msg || 'Something went wrong. Please try again.');
    }
};


  return (
    <div className="form-container">
      <h2>Login</h2>
      <form onSubmit={handleLogin}>
        <div className="form-group">
          <label>Email</label>
          <input
            type="email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
          />
        </div>
        <div className="form-group">
          <label>Password</label>
          <input
            type="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />
        </div>
        {errorMessage && <p className="error-message">{errorMessage}</p>} {/* Display error message */}
        <button type="submit">Login</button>
      </form>
      <div className="form-group">
        <h4>
          New user? <Link to="/register">Register</Link>
        </h4>
      </div>
    </div>
  );
}

export default Login;
