import React from 'react';

function Cart() {
  return (
    <div>
      <h2>Your Cart</h2>
      <p>No items in your cart yet.</p>
    </div>
  );
}

{/* Popup */}
{showPopup && <div className="popup">Product added to cart!</div>}

<div className="cart">
  <h2>Your Cart</h2>
  {cart.length > 0 ? (
    <ul>
      {cart.map((item, index) => (
        <li key={index}>
          {item.name} - ${item.price}
        </li>
      ))}
    </ul>
  ) : (
    <p>Your cart is empty</p>
  )}
</div>
export default Cart;
