import React, { useState, useEffect } from "react";
import axios from "axios";
import "../styles/ManageProducts.css";

const ManageProducts = () => {
  const [products, setProducts] = useState([]);
  const [newProduct, setNewProduct] = useState({ name: "", price: "" });
  const [editProduct, setEditProduct] = useState({ id: "", name: "", price: "" });
  
  useEffect(() => {
    // Fetch products from the database when the component loads
    axios.get("/api/products")
      .then((response) => {
        setProducts(response.data);
      })
      .catch((err) => {
        console.error("Error fetching products:", err);
      });
  }, []);

  const handleAddProduct = () => {
    if (newProduct.name && newProduct.price) {
      axios.post("/api/products", newProduct)
        .then((response) => {
          setProducts([...products, response.data]);
          setNewProduct({ name: "", price: "" }); // Clear the input fields
        })
        .catch((err) => {
          console.error("Error adding product:", err);
        });
    }
  };

  const handleEditProduct = () => {
    if (editProduct.id && editProduct.price) {
      axios.put(`/api/products/${editProduct.id}`, editProduct)
        .then((response) => {
          const updatedProducts = products.map((product) =>
            product.id === editProduct.id ? response.data : product
          );
          setProducts(updatedProducts);
          setEditProduct({ id: "", name: "", price: "" }); // Clear the edit fields
        })
        .catch((err) => {
          console.error("Error editing product:", err);
        });
    }
  };

  const handleDeleteProduct = (id) => {
    axios.delete(`/api/products/${id}`)
      .then(() => {
        setProducts(products.filter((product) => product.id !== id));
      })
      .catch((err) => {
        console.error("Error deleting product:", err);
      });
  };

  return (
    <div className="manage-products">
      <h2>Manage Products</h2>

      <div className="options">
        <h3>Add New Product</h3>
        <input
          type="text"
          placeholder="Product Name"
          value={newProduct.name}
          onChange={(e) => setNewProduct({ ...newProduct, name: e.target.value })}
        />
        <input
          type="number"
          placeholder="Price"
          value={newProduct.price}
          onChange={(e) => setNewProduct({ ...newProduct, price: e.target.value })}
        />
        <button onClick={handleAddProduct}>Add Product</button>
      </div>

      <div className="options">
        <h3>Edit Product Price</h3>
        <select
          value={editProduct.id}
          onChange={(e) => {
            const selectedProduct = products.find(
              (product) => product.id === e.target.value
            );
            setEditProduct({ ...editProduct, id: selectedProduct.id, name: selectedProduct.name, price: selectedProduct.price });
          }}
        >
          <option value="">Select Product</option>
          {products.map((product) => (
            <option key={product.id} value={product.id}>
              {product.name}
            </option>
          ))}
        </select>

        {editProduct.id && (
          <>
            <input
              type="number"
              placeholder="New Price"
              value={editProduct.price}
              onChange={(e) => setEditProduct({ ...editProduct, price: e.target.value })}
            />
            <button onClick={handleEditProduct}>Save Changes</button>
          </>
        )}
      </div>

      <div className="options">
        <h3>Delete Product</h3>
        <select
          onChange={(e) => handleDeleteProduct(e.target.value)}
        >
          <option value="">Select Product to Delete</option>
          {products.map((product) => (
            <option key={product.id} value={product.id}>
              {product.name}
            </option>
          ))}
        </select>
      </div>

      <div className="product-list">
        <h3>Existing Products</h3>
        <ul>
          {products.map((product) => (
            <li key={product.id}>
              {product.name} - ₹{product.price} per kg
              <button onClick={() => handleDeleteProduct(product.id)}>Delete</button>
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
};

export default ManageProducts;
