import React, { useState } from 'react';
import '../styles/AdminDashboard.css';

const AdminDashboard = () => {
  const [currentView, setCurrentView] = useState('');

  const handleViewChange = (view) => {
    setCurrentView(view);
  };

  return (
    <div className="admin-dashboard">
      <h1>Admin Dashboard</h1>
      <div className="dashboard-options">
        <button onClick={() => handleViewChange('users')}>Manage Users</button>
        <button onClick={() => handleViewChange('products')}>Manage Products</button>
      </div>

      <div className="dashboard-content">
        {currentView === 'users' && (
          <div className="manage-users">
            <h2>Manage Users</h2>
            {/* Add user management functionality here */}
            <p>Here you can add, delete, and modify user accounts.</p>
          </div>
        )}

        {currentView === 'products' && (
          <div className="manage-products">
            <h2>Manage Products</h2>
            {/* Add product management functionality here */}
            <p>Here you can add, delete, and modify products.</p>
          </div>
        )}

        {!currentView && <p>Select an option to manage users or products.</p>}
      </div>
    </div>
  );
};

export default AdminDashboard;
