import React, { useState } from 'react';
import '../styles/PaymentSelection.css'; // Import the updated CSS

const PaymentSelection = ({ onSubmit }) => {
  const [paymentMethod, setPaymentMethod] = useState('');

  const handleChange = (e) => {
    setPaymentMethod(e.target.value);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    onSubmit({ paymentMethod }); // Send payment method to parent
  };

  return (
    <form onSubmit={handleSubmit}>
      {/* Add logo at the top */}
      
      <h2>Select Payment Method</h2>
      <label>
        <input
          type="radio"
          value="Stripe"
          checked={paymentMethod === 'Stripe'}
          onChange={handleChange}
        />
        Stripe<div className="logo-container">
        <img src="assets/Logo/stripe.jpeg" alt="Stripe logo" className="logo" />
      </div>
      </label>
      <label>
        <input
          type="radio"
          value="PayPal"
          checked={paymentMethod === 'PayPal'}
          onChange={handleChange}
        />
        PayPal
      </label>
      <button type="submit">Next</button>
    </form>
  );
};

export default PaymentSelection;
