import React, { useState } from 'react';
import '../styles/PaymentSelection.css';

const PaymentSelection = ({ onSubmit, setStep }) => {
  const [paymentMethod, setPaymentMethod] = useState('');

  const handlePaymentChange = (e) => {
    setPaymentMethod(e.target.value);
  };

  const handleFormSubmit = (e) => {
    e.preventDefault();
    if (!paymentMethod) {
      alert('Please select a payment method.');
      return;
    }
    // Pass the selected payment method to the parent component
    onSubmit({ paymentMethod });
    // Move to the next step (order review)
    setStep(3); // Assuming step 3 is OrderReview
  };

  return (
    <form onSubmit={handleFormSubmit}>
      <h2>Select Payment Method</h2>
      <div className="payment-container">
        <label className="payment-option">
          <img
            src="https://upload.wikimedia.org/wikipedia/commons/b/b5/PayPal.svg"
            alt="PayPal"
            className="payment-logo"
          />
          <input
            type="radio"
            name="paymentMethod"
            value="PayPal"
            checked={paymentMethod === 'PayPal'}
            onChange={handlePaymentChange}
          />
          <span>PayPal</span>
        </label>

        <label className="payment-option">
          <img
            src="https://images.stripeassets.com/fzn2n1nzq965/6XFEUA9FzMBMphYdcUab19/37a1e07201366a351f7956560ccac09d/Stripe_wordmark_-_slate.svg?q=80&w=1082"
            alt="Stripe"
            className="payment-logo"
          />
          <input
            type="radio"
            name="paymentMethod"
            value="Stripe"
            checked={paymentMethod === 'Stripe'}
            onChange={handlePaymentChange}
          />
          <span>Stripe</span>
        </label>
      </div>
      <button type="submit" className="submit-btn">
        Next
      </button>
    </form>
  );
};

export default PaymentSelection;
