import React from "react";
import { BrowserRouter as Router, Routes, Route } from "react-router-dom";
import Header from "./components/Header"; // Import your Header component
import AdminDashboard from "./pages/AdminDashboard"; // Main Admin Dashboard
import ManageUsers from "./pages/ManageUsers"; // Manage Users Page
import ManageProducts from "./pages/ManageProducts"; // Manage Products Page
import Login from "./pages/Login";
import Register from "./pages/Register";
import Cart from "./pages/Cart";
import Checkout from "./components/Checkout";
import OrderReview from "./components/OrderReview";
import UserProfile from "./pages/UserProfile";

function App() {
  return (
    <Router>
      <div>
        <Header /> {/* AdminHeader will be used here globally */}
        <Routes>
          <Route path="/" element={<Login />} />
          <Route path="/home" element={<Home />} />
          <Route path="/cart" element={<Cart />} />
          <Route path="/register" element={<Register />} />
          <Route path="/checkout" element={<Checkout />} />
          <Route path="/orderreview" element={<OrderReview />} />
          <Route path="/profile" element={<UserProfile />} />
          <Route path="/admin" element={<AdminDashboard />} />
          <Route path="/admin/users" element={<ManageUsers />} />
          <Route path="/admin/products" element={<ManageProducts />} />
        </Routes>
      </div>
    </Router>
  );
}

export default App;
