import React, { useState } from 'react';
import '../styles/PaymentSelection.css'; // Import the CSS file

const PaymentSelection = ({ onSubmit }) => {
  const [paymentMethod, setPaymentMethod] = useState('');

  const handleChange = (e) => {
    setPaymentMethod(e.target.value);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    onSubmit({ paymentMethod }); // Send payment method to parent
  };

  return (
    <form onSubmit={handleSubmit}>
      <h2>Select Payment Method</h2>
      <label className="payment-option">
        <img
          src="https://upload.wikimedia.org/wikipedia/commons/b/b5/PayPal.svg"
          alt="PayPal Logo"
          className="payment-logo"
        />
        <input
          type="radio"
          value="PayPal"
          checked={paymentMethod === 'PayPal'}
          onChange={handleChange}
        />
        PayPal
      </label>
      <label className="payment-option">
        <img
          src="https://upload.wikimedia.org/wikipedia/commons/4/46/Stripe_Logo%2C_revised_2016.svg"
          alt="Stripe Logo"
          className="payment-logo"
        />
        <input
          type="radio"
          value="Stripe"
          checked={paymentMethod === 'Stripe'}
          onChange={handleChange}
        />
        Stripe
      </label>
      <button type="submit">Next</button>
    </form>
  );
};

export default PaymentSelection;
