import React, { useState } from "react";
import "../styles/ManageProducts.css";

const ManageProducts = () => {
  const [products, setProducts] = useState([
    { id: 1, name: "Fresh Apples", price: 200 },
    { id: 2, name: "Organic Bananas", price: 130 },
    { id: 3, name: "Black Grapes", price: 250 },
    { id: 4, name: "Oranges", price: 140 },
    { id: 5, name: "Watermelon", price: 160 },
    { id: 6, name: "Tomato", price: 60 },
    { id: 7, name: "Carrot", price: 60 },
    { id: 8, name: "Cabbage", price: 40 },
    { id: 9, name: "Potato", price: 45 },
    { id: 10, name: "Onion", price: 70 },
  ]);
  const [newProduct, setNewProduct] = useState({ name: "", price: "" });
  const [editProduct, setEditProduct] = useState(null); // To track the product being edited

  const handleAddProduct = () => {
    if (newProduct.name && newProduct.price) {
      setProducts([...products, { ...newProduct, id: products.length + 1 }]);
      setNewProduct({ name: "", price: "" });
    } else {
      alert("Please provide both product name and price");
    }
  };

  const handleEditProduct = (product) => {
    setEditProduct(product);
  };

  const handleSaveEdit = () => {
    if (editProduct.name && editProduct.price) {
      setProducts(
        products.map((product) =>
          product.id === editProduct.id ? editProduct : product
        )
      );
      setEditProduct(null);
    } else {
      alert("Please provide both product name and price");
    }
  };

  const handleDeleteProduct = (id) => {
    setProducts(products.filter((product) => product.id !== id));
  };

  const handleCancelEdit = () => {
    setEditProduct(null);
  };

  return (
    <div className="manage-products">
      <h2>Manage Products</h2>

      {/* Product List */}
      <ul>
        {products.map((product) => (
          <li key={product.id}>
            {product.name} - â‚¹{product.price}
            <button onClick={() => handleEditProduct(product)}>Edit</button>
            <button onClick={() => handleDeleteProduct(product.id)}>Delete</button>
          </li>
        ))}
      </ul>

      {/* Add New Product Form */}
      <div className="add-form">
        <h3>Add New Product</h3>
        <input
          type="text"
          placeholder="Product Name"
          value={newProduct.name}
          onChange={(e) =>
            setNewProduct((prev) => ({ ...prev, name: e.target.value }))
          }
        />
        <input
          type="number"
          placeholder="Price"
          value={newProduct.price}
          onChange={(e) =>
            setNewProduct((prev) => ({ ...prev, price: e.target.value }))
          }
        />
        <button onClick={handleAddProduct}>Add Product</button>
      </div>

      {/* Edit Product Form */}
      {editProduct && (
        <div className="edit-form">
          <h3>Edit Product</h3>
          <input
            type="text"
            value={editProduct.name}
            onChange={(e) =>
              setEditProduct((prev) => ({ ...prev, name: e.target.value }))
            }
          />
          <input
            type="number"
            value={editProduct.price}
            onChange={(e) =>
              setEditProduct((prev) => ({ ...prev, price: e.target.value }))
            }
          />
          <button onClick={handleSaveEdit}>Save Changes</button>
          <button onClick={handleCancelEdit}>Cancel</button>
        </div>
      )}
    </div>
  );
};

export default ManageProducts;
