import React, { useState, useEffect } from "react";
import axios from "axios";
import "../styles/ManageProducts.css";

const ManageProducts = () => {
  const [products, setProducts] = useState([]);
  const [newProduct, setNewProduct] = useState({ name: "", price: "", img: "" });
  const [editProduct, setEditProduct] = useState(null);

  useEffect(() => {
    // Fetch products from the database
    axios.get("http://localhost:5000/api/products")
      .then(response => {
        setProducts(response.data);
      })
      .catch(error => {
        console.error("Error fetching products:", error);
      });
  }, []);

  const handleAddProduct = () => {
    if (newProduct.name && newProduct.price && newProduct.img) {
      axios.post("http://localhost:5000/api/products", newProduct)
        .then(response => {
          setProducts([...products, response.data]);
          setNewProduct({ name: "", price: "", img: "" });
        })
        .catch(error => {
          console.error("Error adding product:", error);
        });
    } else {
      alert("Please fill in all fields!");
    }
  };

  const handleEditProduct = (product) => {
    setEditProduct(product);
  };

  const handleSaveEdit = () => {
    if (editProduct.name && editProduct.price) {
      axios.put(`http://localhost:5000/api/products/${editProduct.id}`, editProduct)
        .then(response => {
          setProducts(products.map((product) =>
            product.id === editProduct.id ? editProduct : product
          ));
          setEditProduct(null);
        })
        .catch(error => {
          console.error("Error saving product changes:", error);
        });
    } else {
      alert("Please fill in all fields!");
    }
  };

  const handleDeleteProduct = (id) => {
    axios.delete(`http://localhost:5000/api/products/${id}`)
      .then(() => {
        setProducts(products.filter((product) => product.id !== id));
      })
      .catch((error) => {
        console.error("Error deleting product:", error);
      });
  };

  return (
    <div className="manage-products">
      <h2>Manage Products</h2>
      <ul>
        {products.map((product) => (
          <li key={product._id}>
            {product.name} - â‚¹{product.price}
            <button onClick={() => handleEditProduct(product)}>Edit</button>
            <button onClick={() => handleDeleteProduct(product._id)}>Delete</button>
          </li>
        ))}
      </ul>

      {/* Add New Product Form */}
      <div className="add-form">
        <h3>Add New Product</h3>
        <input
          type="text"
          placeholder="Product Name"
          value={newProduct.name}
          onChange={(e) =>
            setNewProduct((prev) => ({ ...prev, name: e.target.value }))
          }
        />
        <input
          type="number"
          placeholder="Price"
          value={newProduct.price}
          onChange={(e) =>
            setNewProduct((prev) => ({ ...prev, price: e.target.value }))
          }
        />
        <input
          type="text"
          placeholder="Image URL"
          value={newProduct.img}
          onChange={(e) =>
            setNewProduct((prev) => ({ ...prev, img: e.target.value }))
          }
        />
        <button onClick={handleAddProduct}>Add Product</button>
      </div>
    </div>
  );
};

export default ManageProducts;
